mapper.select:
select count(*)
from tb_user
where certificates_type = #{certificatesType,jdbcType=VARCHAR}
  and certificates_no = #{certificatesNo,jdbcType=VARCHAR}
  and valid_flag = 0

select count(*)
from tb_user
where certificates_type = #{certificatesType,jdbcType=VARCHAR}
  and certificates_no = #{certificatesNo,jdbcType=VARCHAR}
  and valid_flag = 1
